type.state_script:
FwCaenChannelHV_valueChanged( string domain, string device,
      int actual_dot_status,
      float readBackSettings_dot_i0,
      float readBackSettings_dot_v0,
      float actual_dot_vMon,
      int actual_dot_rampingDirection, string &fwState )
{
	if (
	(actual_dot_status == 1) &&
	(readBackSettings_dot_v0 > 2650) )
	{
		fwState = "ON";
	}
	else if (actual_dot_status == 0)
	{
		fwState = "OFF";
	}
	else if (
	(actual_dot_status == 1) &&
	(readBackSettings_dot_v0 == 0) )
	{
		fwState = "CONFIGURING";
	}
	else if (
	(actual_dot_status == 1) &&
  (readBackSettings_dot_v0 > 200)&&
	(readBackSettings_dot_v0 <= 2650) )
	{
		fwState = "STANDBY";
	}
	else if (actual_dot_status  == 5)
	{
		fwState = "RAMPING_DOWN";
	}
	else if (actual_dot_status == 3)
	{
		fwState = "RAMPING_UP";
	}
	else if (    
	(getBit(actual_dot_status, 3)) ||    //OvC
	(getBit(actual_dot_status, 4)) ||    //OvV 
        (getBit(actual_dot_status, 5)) || //UnV    
        (getBit(actual_dot_status, 12)))  //UnC 
	{
		fwState = "WARNING";
	}
	else if ((getBit(actual_dot_status, 8)) ||	(getBit(actual_dot_status, 9)))
	{
		fwState = "TRIPPED";
	}
	else 
	{
		fwState = "ERROR";
	}
}

type.action_script:
FwCaenChannelHV_doCommand(string domain, string device, string command)
{

      int onoff,OnType;  
      //dyn_string exceptionInfo;
      //dyn_string deviceList;
      //dyn_dyn_mixed recipeObject;
      //dyn_string exceptionInfo;
      int value; 
      string status;
   string sys; 
   sys=getSystemName();
  uint rcpMode;
  bool RcpOK=TRUE;  
 dpGet(sys+"GL1.GEMCAEN.Beam.RcpMode",rcpMode);  
  if (command == "SWITCH_OFF")  
	{

       dpGet(device+".actual.isOn",onoff);
       if(onoff==1)
       dpSet(device+".settings.onOff:_original.._value",0);
       fwDU_getState(domain,device,status);
       fwDU_setState(domain,device,status);

   }  
  if (command == "GO_TO_CONFIGURING")  
	{
       delay(5);
       dpSet(device+".settings.v0",0);
       dpSet(device+".settings.onOff:_original.._value",1);
       fwDU_getState(domain,device,status);
       fwDU_setState(domain,device,status);

   }  
	if (command == "SWITCH_ON")    
	{    
     delay(5);  
     if(rcpMode==2)        //Mode 2 to is Standby and all Hardware contains Stby settings so needs to update to ON settings
     {         
      RcpOK=ChangeBMHV();
      rcpMode=1;
      dpSet(sys+"GL1.GEMCAEN.Beam.RcpMode",rcpMode);  
      DebugN("In Function App ON Rcp");
     }      
      if(RcpOK)
      {
       SwitchOn(device);
       status="ON";
      }
      else
      {
       fwDU_getState(domain,device,status);
      }
      fwDU_setState(domain,device,status);      

  }

  if (command == "GO_TO_STANDBY")    
	 {
     //dyn_string dpe1,dpe2;
     delay(5);
     if(rcpMode!=2)        //Mode 1 to is ON and all Hardware contains On mode settings (cosmic or Physics)
     {
      RcpOK=ApplyStbRcp();
      rcpMode=2; 
      dpSet(sys+"GL1.GEMCAEN.Beam.RcpMode",rcpMode);       
      DebugN("In Function App stb Rcp");
     }
     if(RcpOK)
      {
       SwitchToStby(device);
       status="STANDBY";
      }
      else
      {
       fwDU_getState(domain,device,status);
      }
      fwDU_setState(domain,device,status);
       //dpGet(sys+"GL1.GEMCAEN.Beam.RcpAction",rcpAction); 
      // dpSet(sys+"GL1.GEMCAEN.Beam.StandBy",true);

    }
 }

