type.action_script:
#uses "CMS_GEM_CAEN/GEMFSM.ctl"

FwCaenChannelLvBasic_doCommand(string domain, string device, string command)
{

      int onoff,OnType;  
      //dyn_string exceptionInfo;
      //dyn_string deviceList;
      //dyn_dyn_mixed recipeObject;
      //dyn_string exceptionInfo;
      int value; 
      string status;
   string sys; 
   sys=getSystemName();
     uint rcpMode;
  bool RcpOK=TRUE;  
 dpGet(sys+"GL1.GEMCAEN.Beam.RcpMode",rcpMode);  
  if (command == "SWITCH_OFF")  
	{

       dpGet(device+".actual.isOn",onoff);
       if(onoff==1)
       dpSet(device+".settings.onOff:_original.._value",0);
       fwDU_getState(domain,device,status);
       fwDU_setState(domain,device,status);

   } 
  if (command == "GO_TO_CONFIGURING")
  {
       DebugTN("Entered GO_TO_CONFIGURING action loop");
       delay(5);
       dpSet(device+".settings.v0:_original.._value",0);  
       dpSet(device+".settings.onOff:_original.._value",1);
     } 
 	if (command == "SWITCH_ON" || command == "GO_TO_STANDBY" )    
	{    
      SwitchOn(device);
      status="ON";
      fwDU_setState(domain,device,status);      

  }
}

